def percentages():
	for key in rare_actions:
		s = sum(rare_actions[key])
		p = [rare_actions[key][i]/s for i in range(len(rare_actions[key]))]
		np.round(p, 2, np.array(p))
		print(key, p)


def unusual():
	res = np.zeros([36])
	for key in rare_actions:
		res = np.add(res, rare_actions[key])

	print(np.round(np.divide(res, len(rare_actions)), 2))


if __name__ == '__main__':
	unusual()
